import React, { useState, useEffect } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";

const PropertyManager = () => {
  const [properties, setProperties] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    size: "",
    price: "",
    status: "For Sale",
  });
  const [images, setImages] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/property");
      setProperties(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImages(e.target.files);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach((key) => data.append(key, formData[key]));
    for (let i = 0; i < images.length; i++) {
      data.append("images", images[i]);
    }
    try {
      await axios.post("http://localhost:5000/api/property", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setFormData({ name: "", address: "", size: "", price: "", status: "For Sale" });
      setImages([]);
      fetchProperties();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/property/${id}`);
      fetchProperties();
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpdate = (prop) => {
    setFormData({
      name: prop.name,
      address: prop.address,
      size: prop.size,
      price: prop.price,
      status: prop.status,
    });
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const filteredProperties = properties.filter(
    (p) =>
      p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      p.address.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // PDF Report
  const generatePDF = async () => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("Property Report", 14, 22);
    doc.setFontSize(11);
    doc.setTextColor(100);

    const tableColumn = ["ID", "Name", "Address", "Size", "Price", "Status"];
    const tableRows = [];

    for (const prop of filteredProperties) {
      const rowData = [
        prop.PropertyId,
        prop.name,
        prop.address,
        `${prop.size} sq ft`,
        `$${prop.price}`,
        prop.status,
      ];
      tableRows.push(rowData);
    }

    doc.autoTable({
      startY: 30,
      head: [tableColumn],
      body: tableRows,
      styles: { fontSize: 10 },
      headStyles: { fillColor: [253, 230, 138] }, // gold header
    });

    // Optional: Add images (as small thumbnails)
    let yPos = doc.lastAutoTable.finalY + 10;
    for (const prop of filteredProperties) {
      if (prop.images?.length > 0) {
        doc.text(`Images for ${prop.name}:`, 14, yPos);
        let xPos = 14;
        for (const imgUrl of prop.images) {
          const img = await loadImage(imgUrl);
          doc.addImage(img, "JPEG", xPos, yPos + 2, 30, 20);
          xPos += 32;
        }
        yPos += 25;
        if (yPos > 270) doc.addPage() && (yPos = 20);
      }
    }

    doc.save("property_report.pdf");
  };

  const loadImage = (url) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.setAttribute("crossOrigin", "anonymous");
      img.src = url;
      img.onload = () => resolve(img);
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-gray-900 mb-8">
          Property Management
        </h1>

        {/* Form */}
        <div className="bg-white p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-semibold mb-4">Add / Update Property</h2>
          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Property Name"
              className="p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
              required
            />
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="Address"
              className="p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
              required
            />
            <input
              type="number"
              name="size"
              value={formData.size}
              onChange={handleChange}
              placeholder="Size (sq ft)"
              className="p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
              required
            />
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              placeholder="Price ($)"
              className="p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
              required
            />
            <select
              name="status"
              value={formData.status}
              onChange={handleChange}
              className="p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
            >
              <option>For Sale</option>
              <option>Reserved</option>
              <option>Sold</option>
            </select>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleFileChange}
              className="p-3 border border-dashed rounded-lg cursor-pointer"
            />
            <button
              type="submit"
              className="col-span-1 md:col-span-2 bg-yellow-400 hover:bg-yellow-500 text-black font-semibold py-3 rounded-lg transition"
            >
              Save Property
            </button>
          </form>
        </div>

        {/* Search & Report */}
        <div className="flex justify-between items-center mb-4">
          <input
            type="text"
            placeholder="Search by Name or Address..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="p-3 border rounded-lg w-1/2 focus:outline-none focus:ring-2 focus:ring-yellow-500"
          />
          <button
            onClick={generatePDF}
            className="bg-black text-white px-6 py-2 rounded-lg hover:bg-gray-800 transition"
          >
            Report (PDF)
          </button>
        </div>

        {/* Table */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <table className="min-w-full table-auto">
            <thead className="bg-yellow-400 text-black">
              <tr>
                <th className="py-3 px-4">ID</th>
                <th className="py-3 px-4">Name</th>
                <th className="py-3 px-4">Address</th>
                <th className="py-3 px-4">Size</th>
                <th className="py-3 px-4">Price</th>
                <th className="py-3 px-4">Status</th>
                <th className="py-3 px-4">Images</th>
                <th className="py-3 px-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProperties.map((prop) => (
                <tr key={prop._id} className="hover:bg-gray-50">
                  <td className="py-2 px-4">{prop.PropertyId}</td>
                  <td className="py-2 px-4 font-semibold text-blue-600">{prop.name}</td>
                  <td className="py-2 px-4">{prop.address}</td>
                  <td className="py-2 px-4">{prop.size} sq ft</td>
                  <td className="py-2 px-4 font-semibold text-green-600">${prop.price}</td>
                  <td className="py-2 px-4">
                    <span
                      className={`px-2 py-1 rounded-full text-xs font-semibold ${
                        prop.status === "Sold"
                          ? "bg-red-200 text-red-800"
                          : prop.status === "Reserved"
                          ? "bg-yellow-200 text-yellow-800"
                          : "bg-green-200 text-green-800"
                      }`}
                    >
                      {prop.status}
                    </span>
                  </td>
                  <td className="py-2 px-4 flex flex-wrap gap-2">
                    {prop.images?.map((img, idx) => (
                      <img
                        key={idx}
                        src={img}
                        alt="Property"
                        className="w-16 h-12 object-cover rounded-md shadow-sm hover:scale-110 transition"
                      />
                    ))}
                  </td>
                  <td className="py-2 px-4 flex gap-2">
                    <button
                      onClick={() => handleUpdate(prop)}
                      className="bg-yellow-400 hover:bg-yellow-500 text-black px-3 py-1 rounded-lg text-sm font-semibold transition"
                    >
                      Update
                    </button>
                    <button
                      onClick={() => handleDelete(prop._id)}
                      className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-lg text-sm font-semibold transition"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default PropertyManager;
