import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Container,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Paper,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
} from "@mui/material";

import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

const PropertyManager = () => {
  const [properties, setProperties] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    size: "",
    price: "",
    status: "",
  });
  const [images, setImages] = useState([]);
  const [search, setSearch] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [errors, setErrors] = useState({});

  // Load all properties
  const fetchProperties = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/property");
      setProperties(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProperties();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImages(e.target.files);
  };

  // Validation
  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = "Name is required";
    if (!formData.address.trim()) newErrors.address = "Address is required";
    if (!formData.size || isNaN(formData.size)) newErrors.size = "Valid size is required";
    if (!formData.price || isNaN(formData.price)) newErrors.price = "Valid price is required";
    if (!formData.status) newErrors.status = "Status is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    const data = new FormData();
    data.append("name", formData.name);
    data.append("address", formData.address);
    data.append("size", formData.size);
    data.append("price", formData.price);
    data.append("status", formData.status);

    for (let i = 0; i < images.length; i++) {
      data.append("images", images[i]);
    }

    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/api/property/${editingId}`, data, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        setEditingId(null);
      } else {
        await axios.post("http://localhost:5000/api/property", data, {
          headers: { "Content-Type": "multipart/form-data" },
        });
      }
      setFormData({ name: "", address: "", size: "", price: "", status: "" });
      setImages([]);
      setErrors({});
      fetchProperties();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (prop) => {
    setFormData({
      name: prop.name,
      address: prop.address,
      size: prop.size,
      price: prop.price,
      status: prop.status,
    });
    setEditingId(prop._id);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this property?")) {
      try {
        await axios.delete(`http://localhost:5000/api/property/${id}`);
        fetchProperties();
      } catch (err) {
        console.error(err);
      }
    }
  };

  const handleGeneratePDF = async () => {
    const doc = new jsPDF();

    // Add Landora logo
    const img = new Image();
    img.src = "/Landora.png";
    img.onload = () => {
      doc.addImage(img, "PNG", 15, 10, 40, 25);

      doc.setFontSize(18);
      doc.text("Property Report", 60, 25);

      const timestamp = new Date().toLocaleString();
      doc.setFontSize(10);
      doc.text(`Generated on: ${timestamp}`, 150, 30);

      const tableData = properties.map((p, i) => [
        i + 1,
        p.name,
        p.address,
        p.size,
        p.price,
        p.status,
        // Images will be represented as "Yes" or "No"
        p.images?.length ? "Yes" : "No",
      ]);

      autoTable(doc, {
        startY: 40,
        head: [["#", "Name", "Address", "Size", "Price", "Status", "Images"]],
        body: tableData,
        styles: { cellPadding: 3, fontSize: 9, valign: "middle" },
        headStyles: { fillColor: [63, 81, 181] },
        alternateRowStyles: { fillColor: [240, 240, 240] },
        didDrawPage: (data) => {
          // Footer with page number
          const pageCount = doc.internal.getNumberOfPages();
          for (let i = 1; i <= pageCount; i++) {
            doc.setPage(i);
            doc.setFontSize(9);
            doc.text(`Page ${i} of ${pageCount}`, data.settings.margin.left, doc.internal.pageSize.height - 10);
          }
        },
      });

      // Add images of properties on a new page
      properties.forEach((p) => {
        if (p.images?.length) {
          doc.addPage();
          doc.setFontSize(14);
          doc.text(`Property: ${p.name}`, 14, 20);
          let y = 30;
          p.images.forEach((imgUrl, idx) => {
            doc.addImage(imgUrl, "JPEG", 15, y, 60, 45);
            y += 50;
            if (y > 250) {
              doc.addPage();
              y = 20;
            }
          });
        }
      });

      doc.save("PropertyReport.pdf");
    };
  };

  // Filtering logic
  const filteredProperties = properties.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase()) ||
    p.address.toLowerCase().includes(search.toLowerCase()) ||
    p.status.toLowerCase().includes(search.toLowerCase()) ||
    p.price.toString().includes(search)
  );

  return (
    <div style={{ minHeight: "100vh", background: "#f4f6f9", padding: "2rem", display: 'flex', flexDirection: 'column' }}>
      <Container sx={{ flex: 1 }}>
        <h1 style={{ textAlign: "center", marginBottom: "2rem" }}>
          Property Management System
        </h1>

        {/* Property Form */}
        <div style={{ marginBottom: "2rem", padding: "1.5rem", background: "white", borderRadius: "8px" }}>
          <form onSubmit={handleSubmit}>
            <div style={{ display: "grid", gap: "1rem", gridTemplateColumns: "1fr 1fr" }}>
              <TextField
                label="Property Name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                error={!!errors.name}
                helperText={errors.name}
              />
              <TextField
                label="Address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                error={!!errors.address}
                helperText={errors.address}
              />
              <TextField
                label="Size (sq ft)"
                name="size"
                value={formData.size}
                onChange={handleChange}
                error={!!errors.size}
                helperText={errors.size}
              />
              <TextField
                label="Price ($)"
                name="price"
                value={formData.price}
                onChange={handleChange}
                error={!!errors.price}
                helperText={errors.price}
              />
              <FormControl fullWidth error={!!errors.status}>
                <InputLabel>Status</InputLabel>
                <Select name="status" value={formData.status} onChange={handleChange}>
                  <MenuItem value="For Sale">For Sale</MenuItem>
                  <MenuItem value="Sold">Sold</MenuItem>
                  <MenuItem value="Reserved">Reserved</MenuItem>
                </Select>
                {errors.status && <span style={{ color: "red", fontSize: "0.8rem" }}>{errors.status}</span>}
              </FormControl>
            </div>
            <input type="file" multiple accept="image/*" onChange={handleFileChange} style={{ marginTop: "1rem" }} />
            <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
              {editingId ? "Update Property" : "Add Property"}
            </Button>
          </form>
        </div>

        {/* Search + PDF */}
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "3rem" }}>
          <TextField
            label="Search Properties"
            variant="outlined"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <Button variant="contained" color="secondary" onClick={handleGeneratePDF}>
            Generate PDF
          </Button>
        </div>

        {/* Properties Table */}
        <Paper>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Property ID</TableCell>
                <TableCell>Name</TableCell>
                <TableCell>Address</TableCell>
                <TableCell>Size</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Images</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredProperties.map((prop) => (
                <TableRow key={prop._id}>
                  <TableCell>{prop.PropertyId}</TableCell>
                  <TableCell>{prop.name}</TableCell>
                  <TableCell>{prop.address}</TableCell>
                  <TableCell>{prop.size} sq ft</TableCell>
                  <TableCell>${prop.price}</TableCell>
                  <TableCell>{prop.status}</TableCell>
                  <TableCell>
                    {prop.images?.map((img, i) => (
                      <img key={i} src={img} alt="property" width="60" height="45" style={{ marginRight: "4px" }} />
                    ))}
                  </TableCell>
                  <TableCell>
                    <Button
                      size="small"
                      variant="outlined"
                      onClick={() => handleEdit(prop)}
                      sx={{ marginRight: 1 }}
                    >
                      Update
                    </Button>
                    <Button
                      size="small"
                      color="error"
                      variant="outlined"
                      onClick={() => handleDelete(prop._id)}
                    >
                      Delete
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      </Container>
    </div>
  );
};

export default PropertyManager;
