// src/pages/Login.js
import React, { useState } from "react";
import {
  TextField,
  Button,
  Card,
  CardContent,
  Typography,
  CircularProgress,
} from "@mui/material";
import axios from "axios";

const Login = () => {
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // ✅ Update state when inputs change
  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  // ✅ Handle login
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    // Frontend validation
    if (!formData.username.trim() || !formData.password.trim()) {
      setError("Both username and password are required!");
      return;
    }

    setLoading(true);
    try {
  // Use backend at localhost:5000
  const res = await axios.post("http://localhost:5000/api/auth/login", formData);

      if (res.data?.token) {
        localStorage.setItem("token", res.data.token); // Save JWT
        alert("✅ Login successful!");
        window.location.href = "/dashboard"; // Redirect
      } else {
        setError("Invalid response from server.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "❌ Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #131313 60%, #ff932f 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Card sx={{ width: 380, boxShadow: 6, borderRadius: 4 }}>
        <CardContent>
          <Typography variant="h5" align="center" gutterBottom sx={{ color: '#ff932f', fontWeight: 700 }}>
            Login
          </Typography>
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 18 }}>
            <TextField
              label="Username"
              name="username"
              fullWidth
              value={formData.username}
              onChange={handleChange}
              sx={{ mb: 2 }}
            />
            <TextField
              label="Password"
              name="password"
              type="password"
              fullWidth
              value={formData.password}
              onChange={handleChange}
              sx={{ mb: 2 }}
            />
            {error && (
              <Typography color="error" variant="body2" align="center" sx={{ mb: 1 }}>
                {error}
              </Typography>
            )}
            <Button
              type="submit"
              variant="contained"
              disabled={loading}
              sx={{
                mt: 1,
                background: '#ff932f',
                color: '#131313',
                fontWeight: 700,
                '&:hover': { background: '#131313', color: '#ff932f' },
                borderRadius: 2,
              }}
              fullWidth
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : "Login"}
            </Button>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Login;
